    generate() {
        return yaml.safeDump(generate(this.spec), {
            indent: 4,
            lineWidth: 1024,
            schema: YAML_SCHEMA
        }).replace(/^(\s*)\'#.*?\':\s*\'*(.*?)\'*$/mg, '$1# $2'); // make comment lines
