module.exports = function (ctx, locals) {
  var get_config = ctx.get_config,
      has_config = ctx.has_config,
      has_widget = ctx.has_widget,
      is_same_link = ctx.is_same_link,
      page = ctx.page;
  var menus = get_config('navbar.menu', {});
  var actives = {};
  Object.keys(menus).forEach(function (i) {
    actives[i] = typeof page.path !== 'undefined' && is_same_link(menus[i], page.path);
  });
  var hasToc = get_config('toc') === true && has_widget('toc') && (page.layout === 'page' || page.layout === 'post');
  return Object.assign(locals, {
    hasToc: hasToc,
    menus: menus,
    actives: actives,
    title: get_config('title'),
    logo: get_config('logo'),
    links: get_config('navbar.links', []),
    search: has_config('search.type')
  });
};
