"use strict";

/**
* Helper functions for page/post.
*
* @example
*     <%- is_categories(page) %>
*     <%- is_tags(page) %>
*/
module.exports = function (hexo) {
  hexo.extend.helper.register('is_categories', function () {
    var page = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : null;
    return (page === null ? this.page : page).__categories === true;
  });
  hexo.extend.helper.register('is_tags', function () {
    var page = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : null;
    return (page === null ? this.page : page).__tags === true;
  });
};